import React from 'react'
import Header from './Header'
import '../App.css'

function Login(props){
 return(
 <div className="form-group add-form" style={{width:"80%"}}>
            <span className="brand d-flex text-light shadow">N</span>
            <input placeholder="Username" className="form-control text-light"    style={{marginBottom:"2vh",backgroundColor:"rgb(255,255,255,0)",borderColor:"white"}}  onChange={this.amountup}/>
            <input placeholder="Password" className="form-control text-light" style={{marginBottom:"2vh",backgroundColor:"rgb(255,255,255,0)",borderColor:"white",borderSize:"2px"}} onChange={this.joinup}/>
            <div className="d-flex justify-content-center">
            <button className="btn btn-outline-danger" onClick={this.dataup}>SignIn</button>
            <button className="btn btn-outline-success ml-2" onClick={this.dataup}>SignUp</button>
            </div>
            </div>
);
}

class Add extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            amount:0,
            joins:0,
            users:0
        }
        this.amountup=this.amountup.bind(this)
        this.joinup=this.joinup.bind(this)
        this.userup=this.userup.bind(this)
        this.dataup=this.dataup.bind(this)
    }

    async dataup(){
      var amount=await this.state.amount;
      var joins=await this.state.joins;
      var users=await this.state.users;
      this.props.putData(amount,joins,users)
    }

    async amountup(e){
        await this.setState({amount:e.target.value})
    }

    async joinup(e){
        await this.setState({joins:e.target.value})
    }

    async userup(e){
        await this.setState({users:e.target.value})
    }

    render(){
        return (
            <div style={{background:"url('stranger3.jpg') no-repeat",backgroundSize:"120vw 100vh",height:'100vh'}}>
            <div className="container d-flex align-items-center Add-div " style={{height:"100vh",width:"100vw"}}>
            <div className="form shadow d-flex justify-content-center align-items-center" style={{backgroundColor:"rgb(71,64,95,0.9)"}}>
            <Login />
	    </div>
            </div>
            </div>
        )
    }
}
export default Add;
