import React from 'react'
class Add extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            amount:0,
            joins:0,
            users:0
        }
        this.amountup=this.amountup.bind(this)
        this.joinup=this.joinup.bind(this)
        this.userup=this.userup.bind(this)
        this.dataup=this.dataup.bind(this)
    }

    dataup(){
      var amount=this.state.amount
      var joins=this.state.joins
      var users=this.state.users
      fetch(`localhost:9000/testAPI/add?amount=${amount}&joins=${joins}&users=${users}`)
      .then(res => res.text())
      .then(r => console.log(r))
      .catch(err => {console.log(err.message)})
    }

    async amountup(e){
        await this.setState({amount:e.target.value})
        console.log(this.state.amount);
    }

    joinup(e){
        this.setState({joins:e.target.value})
    }

    userup(e){
        this.setState({users:e.target.value})
    }

    render(){
        return (
            <div className="form-group">
            <input placeholder="Amount" className="form-control" onChange={this.amountup}/>
            <input placeholder="Join" className="form-control" onChange={this.joinup}/>
            <input placeholder="Users" className="form-control" onChange={this.userup}/>
            <button className="btn btn-primary" onClick={this.dataup}>Submit</button>
            </div>
        )
    }
}
export default Add;
