import React from 'react';
import '../App.css';
import '../fontawesome/css/all.css';
import Header from './Header';
import CardList from './Card';
import { navigate,Router } from '@reach/router';
import Add from './Add'
import { FaRupeeSign } from 'react-icons/fa'

function Cardlist(props){
    return(
        <div> 
        <Header />
        <div className="container" style={{marginTop:"66px"}}>
         <CardList getData={props.getData} data={props.data} color={props.color} fontsizeL={props.fontsizeL} fontsizeS={props.fontsizeS} border={props.border}/>
        </div>
        <div className="fixed-bottom d-flex justify-content-center">
            <div className="badge bg-warning d-flex align-items-center justify-content-center text-light" onClick={data} style={{height:"40px",fontSize:"16px",width:"100px"}}><FaRupeeSign /> SELL</div>
        </div>
        </div>
    );
}

function data(){
    navigate('register')
   }

class App extends React.Component {
    constructor(props){
        super(props)
        this.state={
            data:[],
            key:0
        }
        this.getData=this.getData.bind(this)
        this.putData=this.putData.bind(this)
    }
    componentDidMount(){
        this.getData()
    }
    async getData() {
        await fetch("http://localhost:9000/")
            .then(res => res.json())
            .then(res => {
               this.setState({data: res.data})
               var x=this.state.key+1
               this.setState({key:x})
              });
          }

    async putData(amount,joins,users){
    await fetch(`http://localhost:9000/add?amount=${amount}&joins=${joins}&users=${users}`)
      .then(res => res.text())
      .then(r => {
          console.log(r);
          this.getData();
          navigate('/')
        })
      .catch(err => {console.log(err.message)})
    }
   render(){
    return(
        <Router>
        <Cardlist path="/" getData={this.getData} key={this.state.key} data={this.state.data} color="rgb(71,64,65)" fontsizeL="24px" fontsizeS="12px" border="10px" />
        <Add path="/register" putData={this.putData} data={this.state.data} />
        </Router>
       );
   }
}


export default App;
