import React from 'react'
import Header from './Header'
import '../App.css'

function Login(props){
 return(
 <div className="form-group add-form" style={{width:"80%"}}>
            <span className="brand d-flex text-light shadow">N</span>
            <input placeholder="Username" className="form-control text-light" style={{marginBottom:"2vh",backgroundColor:"rgb(255,255,255,0)",borderColor:"white"}}/>
            <input placeholder="Password" className="form-control text-light" style={{marginBottom:"2vh",backgroundColor:"rgb(255,255,255,0)",borderColor:"white",borderSize:"2px"}}/>
            <div className="d-flex justify-content-center">
            <button className="btn btn-outline-danger">SignIn</button>
            <button className="btn btn-outline-success ml-2" onClick={props.initSignUp}>SignUp</button>
            </div>
            </div>
);
}

function SignUp(props){
    return(
    <div className="form-group add-form" style={{width:"80%"}}>
               <span className="brand d-flex text-light shadow">N</span>
               <input placeholder="Email" className="form-control text-light" style={{marginBottom:"2vh",backgroundColor:"rgb(255,255,255,0)",borderColor:"white"}}/>
               <input placeholder="Password" className="form-control text-light" style={{marginBottom:"2vh",backgroundColor:"rgb(255,255,255,0)",borderColor:"white",borderSize:"2px"}}/>
               <input placeholder="Repeat Password" className="form-control text-light" style={{marginBottom:"2vh",backgroundColor:"rgb(255,255,255,0)",borderColor:"white",borderSize:"2px"}}/>
               <div className="d-flex justify-content-center">
               <button className="btn btn-outline-success ml-2">SignUp</button>
               </div>
               </div>
   );
   }

   function RegisterForm(props){
       return(
        <div className="container d-flex align-items-center Add-div " style={{height:"100vh",width:"100vw"}}>
        <div className="form shadow d-flex justify-content-center align-items-center" style={{backgroundColor:"rgb(71,64,95,0.9)"}}>
        {(!props.signup)?<Login initSignUp={props.initSignUp}/>:<SignUp />}
        </div>
        </div>
       )
   }

   function DashBoard(props){
       return(
        <div className="container d-flex align-items-center" style={{height:"100vh",width:"100vw"}}>
        <div className="shadow d-flex justify-content-center align-items-center" style={{backgroundColor:"rgb(71,64,95,0.9)",height:"80vh",width:"80vw"}}>
         DASHBOARD!
        </div>
        </div>
       )
   }

class Add extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            amount:0,
            joins:0,
            users:0,
            signup:false,
            loggedIn:false
        }
        this.amountup=this.amountup.bind(this)
        this.SignUpSuccess=this.SignUpSuccess.bind(this)
        this.initSignUp=this.initSignUp.bind(this)
        this.dataup=this.dataup.bind(this)
    }
    async dataup(){
      var amount=await this.state.amount;
      var joins=await this.state.joins;
      var users=await this.state.users;
      this.props.putData(amount,joins,users)
    }

    async amountup(e){
        await this.setState({amount:e.target.value})
    }

    async SignUpSuccess(e){
        await this.setState({loggedIn:true})
    }

    async initSignUp(e){
        await this.setState({signup:true})
    }

    render(){
        return (
            <div style={{background:"url('stranger3.jpg') no-repeat",backgroundSize:"120vw 100vh",height:'100vh'}}>
            {(!this.loggedIn)?<RegisterForm initSignUp={this.initSignUp} signup={this.state.signup}/>:<DashBoard />} 
            </div>
        )
    }
}
export default Add;
