import React from 'react';
import '../App.css'
import Connection from './data'
import './data'

class Card extends React.Component{
    constructor(props){
        super(props)
        this.state ={
            widthv:0,
            width: "0%",
            users: this.props.users
        }
        this.clicked=this.clicked.bind(this)
    }
    async clicked(){
        var added=(this.state.widthv+(100/this.props.users))
        await this.setState({widthv: added});
        var str=this.state.widthv.toString()+"%"
        this.setState({width: str});
        if(this.state.users>0){
        var newuser=this.state.users-1;
        this.setState({users: newuser })
        }
    }
    render(){
        return (
         <div className="item-card mb-2 shadow">
             <div className="p-2">
             <div className="row text-light">
                <div className="col-1">

                </div>
                <div className="col-7 heading">
                    <div>
                    <span className="d-flex acc">Netflix Account</span>
                    <span className="d-flex vertical-align-center mt-1" style={{fontSize:"24px"}}>₹{this.props.amount}</span>
                    </div>
                </div>
                <div className="col-3 d-flex justify-content-end">
                <div>
                <span className="d-flex justify-content-end" style={{fontSize:12}}>Join</span>
                <div className="mt-1 badge bg-success d-flex align-items-center justify-content-center" onClick={this.clicked}>₹{this.props.join}</div>
                </div>
                </div>
                <div className="col-2">

                </div>
             </div>
             <div className="row p-2 no-gutters">
             <div className="col-1">

             </div>
             <div className="col-10">
             <div className="progress" style={{height:"10px"}}>
                <div className="progress-bar bg-warning" role="progressbar" aria-valuenow="75" aria-valuemin="0" aria-valuemax="100" style={{width:this.state.width}}></div>
                </div>
                </div>
             </div>
             <div className="row">
                 <div className="col-1" />
                 <div className="col-5 text-warning">
                   {this.state.users} users left
                 </div>
                 <div className="col-5 d-flex justify-content-end text-light">
                    {this.props.users} users
                 </div>
                 <div className="col-1" />
             </div>
             </div>
        </div>
        );
    }
}

class CardList extends React.Component{
constructor(props){
    super(props)
    this.state={
        data: []
    }
    this.list=this.list.bind(this)
}
componentDidMount(){
  var x=new Connection();
  x.connectdb().Query("SELECT * FROM bflix");
}
list(props){
    var arr=this.state.data
    return arr.map(item=>{
        var amounts=item.amount;
        var joins=item.join;
        var users=item.users;
        return <Card amount={amounts} join={joins} users={users} />
    })
}
render(){
  return(
      <this.list />
      );
}
}
export default CardList;
