import React from 'react';
import '../App.css'

class Card extends React.Component{
    constructor(props){
        super(props)
        this.state ={
            widthv:0,
            width: "0%",
            users: this.props.users
        }
        this.clicked=this.clicked.bind(this)
    }
    async clicked(){
        var added=(this.state.widthv+(100/this.props.users))
        await this.setState({widthv: added});
        var str=this.state.widthv.toString()+"%"
        this.setState({width: str});
        if(this.state.users>0){
        var newuser=this.state.users-1;
        this.setState({users: newuser })
        fetch(`http://localhost:9000/add?width=${this.state.width}&id=${this.props.id}`)
        .then(
            res => res.text()
        ).then(
            result => console.log(result)
        )
        }
    }
    render(){
        return (
         <div className="item-card mb-2 shadow" style={{backgroundColor:this.props.color,borderRadius:this.props.border}}>
             <div className="p-2">
             <div className="row text-light">
                <div className="col-1">

                </div>
                <div className="col-7 heading">
                    <div>
                    <span className="d-flex acc" style={{fontSize:this.props.fontsizeL}}>Netflix Account</span>
                    <span className="d-flex vertical-align-center mt-1" style={{fontSize:this.props.fontsizeL}}>₹{this.props.amount}</span>
                    </div>
                </div>
                <div className="col-3 d-flex justify-content-end">
                {!this.props.hide && <div>
                <span className="d-flex justify-content-end" style={{fontSize:this.props.fontsizeS}}>Join</span>
                <div className="mt-1 badge bg-success d-flex align-items-center justify-content-center" onClick={this.clicked}>₹{this.props.join}</div>
                </div>}
                </div>
                <div className="col-2">

                </div>
             </div>
             <div className="row p-2 no-gutters">
             <div className="col-1">

             </div>
             <div className="col-10">
             <div className="progress" style={{height:"10px"}}>
                <div className="progress-bar bg-warning" role="progressbar" aria-valuenow="75" aria-valuemin="0" aria-valuemax="100" style={{width:this.state.width}}></div>
                </div>
                </div>
             </div>
             <div className="row">
                 <div className="col-1" />
                 <div className="col-5 text-warning" style={{fontSize:this.props.fontsizeL}}>
                   {this.state.users} users left
                 </div>
                 <div className="col-5 d-flex justify-content-end text-light" style={{fontSize:this.props.fontsizeL}}>
                    {this.props.users} users
                 </div>
                 <div className="col-1" />
             </div>
             </div>
        </div>
        );
    }
}

class CardList extends React.Component{
constructor(props){
    super(props)
    this.list=this.list.bind(this)
}
list(props){
    var arr=props.data
    var color=props.color
    console.log(color)
    return arr.map(item=>{
        var amounts=item.amount;
        var joins=item.joins;
        var users=item.users;
        var key=item.id;
        var width=item.width;
        return <Card id={key} width={width} key={key} amount={amounts} join={joins} users={users} color={color} fontsizeL={props.fontsizeL} fontsizeS={props.fontsizeS} border={props.border} hide={props.hide}/>
    })
}
render(){
  return(
      <this.list data={this.props.data} color={this.props.color} fontsizeL={this.props.fontsizeL} fontsizeS={this.props.fontsizeS} border={this.props.border} hide={this.props.hide}/>
      );
}
}
export default CardList;
