import React from 'react'
import CardList from './Card'
import AddItem from './AddItem'
import { FaPlusCircle,FaSmile,FaUserCircle,FaInbox,FaAd } from 'react-icons/fa'
class CreateAd extends React.Component{
  constructor(props){
    super(props)
    this.state={
      add:false
    }
    this.profile=this.profile.bind(this);
    this.addItem=this.addItem.bind(this);
    this.switchScreen=this.switchScreen.bind(this)
  }
  addItem(e){
    this.setState({add:true})
  }
  profile(e){
    e.target.style.color="white"
    this.switchScreen('profile');
  }
  switchScreen(param){
    switch(param) {
      case 'profile':
       return 'profile';
      case 'inbox':
       return 'inbox';
      case 'add':
       return 'add';
      case 'ad':
        return 'ad';
      default:
        return 'add';
      }
  }
  render(){
  return(
    <div className="col-12 col-sm-12 shadow" style={{width:"50%",height:"100%"}}>
      <div className="row" style={{width:"100%",zIndex:1,heigth:"20%"}}>
        <div className="col-3 d-flex justify-content-center align-items-center" id="profile" onClick={this.profile}><FaUserCircle style={{color:"rgb(0,233,233)",height:"20%",width:"20%",cursor:"pointer",minHeight:"20px",minWidth:"20px"}}/></div>
        <div className="col-3 d-flex justify-content-center align-items-center" id="inbox" onClick={this.inbox}><FaInbox style={{color:"rgb(0,233,233)",height:"20%",width:"20%",cursor:"pointer",minHeight:"20px",minWidth:"20px"}}/></div>
        <div className="col-3 d-flex justify-content-center align-items-center" id="add" onClick={this.add}><FaPlusCircle style={{color:"rgb(0,233,233)",height:"20%",width:"20%",cursor:"pointer",minHeight:"20px",minWidth:"20px"}}/></div>
        <div className="col-3 d-flex justify-content-center align-items-center" id="myAds" onClick={this.myAds}><FaAd style={{color:"rgb(0,233,233)",height:"20%",width:"20%",cursor:"pointer",minHeight:"20px",minWidth:"20px"}}/></div>
      </div>
      <div className="row d-flex justify-content-center" style={{width:"100%",zIndex:2,height:"80%"}}>
      {this.switchScreen()}
      </div>
    </div> 
  )
  }
}


function MyAds(props){
  return(
      <div className="col-5 col-sm-3" id="demo" style={{width:"50%",height:"100%",overflowY:"auto"}}>
          <CardList data={props.data} hide={true} fontsizeL="16px" fontsizeS="10px" progressBarHeight="5px"/>
      </div>
  )
}
class DashBoard extends React.Component{
    constructor(props){
        super(props)
    }
    render(){
        return (
        <div className="d-flex align-items-center justify-content-center" style={{height:"100vh",width:"100vw"}}>
        <div className="shadow row" style={{backgroundColor:"rgb(71,64,95,0.9)",height:"100vh",width:"100vw"}}>
        <CreateAd />
        </div>
        </div>
        )
    }
}

export default DashBoard;
