import React from 'react'
class Add extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            amount:0,
            joins:0,
            users:0
        }
        this.amountup=this.amountup.bind(this)
        this.joinup=this.joinup.bind(this)
        this.userup=this.userup.bind(this)
        this.dataup=this.dataup.bind(this)
    }

    async dataup(){
      var amount=await this.state.amount;
      var joins=await this.state.joins;
      var users=await this.state.users;
      this.props.putData(amount,joins,users)
    }

    async amountup(e){
        await this.setState({amount:e.target.value})
    }

    async joinup(e){
        await this.setState({joins:e.target.value})
    }

    async userup(e){
        await this.setState({users:e.target.value})
    }

    render(){
        return (
            <div style={{background:"url('peaky.jpg') no-repeat",backgroundSize:"100vw 100vh",height:'100vh'}}>
            <div className="container d-flex align-items-center" style={{height:"100vh",width:"100vw"}}>
            <div className="form shadow d-flex justify-content-center align-items-center" style={{width:"30vw",height:"70vh"}}>
            <div className="form-group" style={{width:"90%"}}>
            <input placeholder="Amount" className="form-control" style={{marginBottom:"5vh"}} onChange={this.amountup}/>
            <input placeholder="Join" className="form-control" onChange={this.joinup}/>
            <input placeholder="Users" className="form-control" onChange={this.userup}/>
            <button className="btn btn-primary" onClick={this.dataup}>Submit</button>
            </div>
	    </div>
            </div>
            </div>
        )
    }
}
export default Add;
