import React,{ Component } from 'react';
import Header from './Header';
import { navigate } from '@reach/router'
import '../App.css';
import firebase from './firebaseconfig';
import 'firebaseui';
var ui = new firebaseui.auth.AuthUI(firebase.auth())
class Register extends Component{
    constructor(props){
        super(props);
        this.state={
            email:null,
            password:null,
            error:null,
            users:null

        }
        this.handleReg=this.handleReg.bind(this);
        this.handleMail=this.handleMail.bind(this);
        this.handlePass=this.handlePass.bind(this);
    }
    componentDidMount(){
        if(firebase.auth().currentUser){
            navigate('/login');
        }
    }
    handleReg(e){
        var mail=this.state.email;
        var password=this.state.password;
        firebase.auth().signInWithEmailAndPassword(mail,password).then(user => {
          this.setState({error:null});
          this.setState({users:user});
          navigate('/login');
        }).catch(function(err){
            var msg=err.message;
            this.setState({error:msg});
            console.log(err);
        }.bind(this))
    }
    handleMail(e){
        var x=e.target.value;
        this.setState({email:x});
    }
    handlePass(e){
        var x=e.target.value;
        this.setState({password:x});
    }
render(){
    return(
        <div className="container">
        <div className="form" style={{width:"28%",margin:"30vh auto"}}>
            <div className="input-group mb-3 d-flex justify-content-center">
               <img src="logo.png" alt="logo"/>
            </div>
            {(this.state.error) && 
                <div className="input-group mb-3">
                    <textarea type="text" className="text-danger is-invalid form-control text-danger" value={this.state.error} disabled></textarea>
                </div>
            }
            <div className="input-group mb-3">
                <input type="text" placeholder="Username" className="form-control" />
            </div>
            <div className="input-group mb-3">
                <input type="text" placeholder="Email" className="form-control" onChange={this.handleMail}/>
            </div>
            <div className="input-group mb-3">
                <input type="password" placeholder="Password" className="form-control" onChange={this.handlePass}/>
            </div> 
            <div  className="d-flex justify-content-center">
            <button className="btn btn-outline-success" onClick={this.handleReg}>Sign Up</button>
            </div>
            </div>
            </div>
    );
}
}
ui.start('#firebaseui-auth-container', {
  signInOptions: [
    {
      provider: firebase.auth.EmailAuthProvider.PROVIDER_ID,
      signInMethod: firebase.auth.EmailAuthProvider.EMAIL_LINK_SIGN_IN_METHOD,
      // Allow the user the ability to complete sign-in cross device,
      // including the mobile apps specified in the ActionCodeSettings
      // object below.
      forceSameDevice: false,
      // Used to define the optional firebase.auth.ActionCodeSettings if
      // additional state needs to be passed along request and whether to open
      // the link in a mobile app if it is installed.
      emailLinkSignIn: function() {
        return {
          // Additional state showPromo=1234 can be retrieved from URL on
          // sign-in completion in signInSuccess callback by checking
          // window.location.href.
          url: 'https://www.example.com/completeSignIn?showPromo=1234',
          // Custom FDL domain.
          dynamicLinkDomain: 'example.page.link',
          // Always true for email link sign-in.
          handleCodeInApp: true,
          // Whether to handle link in iOS app if installed.
          iOS: {
            bundleId: 'com.example.ios'
          },
          // Whether to handle link in Android app if opened in an Android
          // device.
          android: {
            packageName: 'com.example.android',
            installApp: true,
            minimumVersion: '12'
          }
        };
      }
    }
  ]
});
class SignUp extends Component {
    render(){
        return(
            <div>
            <Header />
            <Register />
            </div>
        );
    }
}
export default SignUp;
