import React from 'react'
import Header from './Header'
import '../App.css'

class Add extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            amount:0,
            joins:0,
            users:0
        }
        this.amountup=this.amountup.bind(this)
        this.joinup=this.joinup.bind(this)
        this.userup=this.userup.bind(this)
        this.dataup=this.dataup.bind(this)
    }

    async dataup(){
      var amount=await this.state.amount;
      var joins=await this.state.joins;
      var users=await this.state.users;
      this.props.putData(amount,joins,users)
    }

    async amountup(e){
        await this.setState({amount:e.target.value})
    }

    async joinup(e){
        await this.setState({joins:e.target.value})
    }

    async userup(e){
        await this.setState({users:e.target.value})
    }

    render(){
        return (
            <div style={{background:"url('stranger2.png') no-repeat",backgroundSize:"100vw 100vh",height:'100vh'}}>
            <Header />
            <div className="container d-flex align-items-center" style={{height:"100vh",width:"100vw"}}>
            <div className="form shadow d-flex justify-content-center align-items-center" style={{height:"70vh",backgroundColor:"rgb(91,64,60,0.3)",borderRadius:"10px"}}>
            <div className="form-group add-form" style={{width:"80%"}}>
            <input placeholder="Amount" className="form-control text-light" style={{marginBottom:"2vh",backgroundColor:"rgb(255,255,255,0)",borderColor:"rgb(223,0,0)",borderSize:"16px"}}  onChange={this.amountup}/>
            <input placeholder="Join" className="form-control text-light" style={{marginBottom:"2vh",backgroundColor:"rgb(255,255,255,0)",borderColor:"white",borderSize:"2px"}} onChange={this.joinup}/>
            <input placeholder="Users" className="form-control text-light" style={{marginBottom:"2vh",backgroundColor:"rgb(255,255,255,0)",borderColor:"white",borderSize:"2px"}} onChange={this.userup}/>
            <div className="d-flex justify-content-center">
            <button className="btn btn-outline-light" onClick={this.dataup}>Submit</button>
            </div>
            </div>
	    </div>
            </div>
            </div>
        )
    }
}
export default Add;
