import React from 'react';
import './App.css';
import Reviews from './Reviews'
import './fontawesome/css/all.css';

class Header extends React.Component {
    render(){
        return (
            <header className="fixed-top">
<div className="navbar navbar-expand-sm navbar-dark">
<div className="container"><span className="navbar-brand">
अन्नpoorna Kitchen
</span>
<button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#mynav"><span className="navbar-toggler-icon" /></button>
<div className="collapse navbar-collapse" id="mynav">
<nav className="navbar-nav">
<a href="index.html" className="nav-item nav-link">Home</a>
<a href="order.html" className="nav-item nav-link">Order</a>
<a href="#rev" className="nav-item nav-link">Reviews</a>
<a href="#contact" className="nav-item nav-link">Contact Us</a>
</nav>
</div>
</div>
</div>
</header>
        );
    }
}



class Jumbotron extends React.Component{
render(){
    return (
        <div className="jumbo">
        <div className="container">
        <div style={{position:"absolute", marginTop:"8%"}} >
        <span className="bg-dark text-light" style={{fontSize:"32px"}} >Welcome to अन्नPoorna Kitchen</span><br />
        <blockquote className="blockquote">Lorem Ipsum Dor Dodot Solor Amit<br />Blah blah blah my name is<br />Reshabh Shukla</blockquote>
        <a href="order.html"><button className="btn btn-success" style={{marginLeft:"16%", marginTop:"2%"}}>Book a Meal!</button></a>
        </div>
        </div>
        <div className="jumbotron-img">
        <img src="banner.jpg" style={{width:"100%",height:"100%"}} />
        </div>
        <div className="container text-center" id="rev">
        <em style={{fontSize:"56px"}}>People love <span className="text-success">our food</span></em>
        <br /><br /><br />
        </div>
        </div>

    );
}
}


class Footer extends React.Component {
    render(){
        return (
            <footer className="page-footer font-small indigo" id="contact" >
    <br /><br />
    <div className="gradient"></div>
        <div className="container">
          <div className="row text-center d-flex justify-content-center">

            <div className="col-md-12">
                <a href="index.html"><span className="text-light footer-logo"><u>अन्नpoorna Kitchen</u></span></a>
            </div>

          </div>
          <div className="row d-flex text-center justify-content-center mb-md-0 pb-4">

            <div className="col-md-8 col-12">
              <address className="text-dark">
                  <p><span>Annapoorna Tiffin Service, 483 Shyam Nagar, Kanpur</span><br />
                  <span>Opening Hours : Mo-Su 8:00 a.m.-9:00 p.m.</span><br />
                  <span>Call for Offline Bookings : 831 806 8232</span></p>
              </address>
            </div>

          </div>
          </div>
      </footer>
        );
    }
    }



class App extends React.Component {
render(){ 
 return (<div>
     <Header />
     <Jumbotron />
     <Reviews />
     <Footer />
 </div>);
}
}

export default App;
