import React from 'react'
import CardList from './Card'
import AddItem from './AddItem'
import { FaPlusCircle,FaSmile } from 'react-icons/fa'
class CreateAd extends React.Component{
  constructor(props){
    super(props)
    this.state={
      add:false
    }
    this.addItem=this.addItem.bind(this);
  }
  addItem(e){
    this.setState({add:true})
  }
  render(){
  return(
    <div className="col-md-8 d-flex justify-content-center align-items-center shadow" style={{width:"50%",height:"100%"}}>
    {(!this.state.add)?<FaPlusCircle style={{color:"rgb(0,233,233)",height:"20%",width:"20%",cursor:"pointer"}} onClick={this.addItem}/>:<AddItem />}
    </div> 
  )
  }
}


function MyAds(props){
  return(
      <div className="col-md-4" style={{width:"50%",height:"100%",overflowY:"auto"}}>
          <CardList data={props.data} hide={true} fontsizeL="16px" fontsizeS="10px" progressBarHeight="5px"/>
      </div>
  )
}
class DashBoard extends React.Component{
    constructor(props){
        super(props)
    }
    render(){
        return (
        <div className="container d-flex align-items-center" style={{height:"100vh",width:"100vw"}}>
        <div className="shadow row" style={{backgroundColor:"rgb(71,64,95,0.9)",height:"95vh",width:"95vw"}}>
        <CreateAd />
        <MyAds data={this.props.data}/>
        </div>
        </div>
        )
    }
}

export default DashBoard;
