import React from 'react'
class Add extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            amount:0,
            joins:0,
            users:0
        }
        this.amountup=this.amountup.bind(this)
        this.joinup=this.joinup.bind(this)
        this.userup=this.userup.bind(this)
        this.dataup=this.dataup.bind(this)
    }

    dataup(){
      var amount=this.state.amount
      var joins=this.state.joins
      var users=this.state.users
      fetch(`localhost:9000/testAPI/add?amount=${amount}&joins=${joins}&users=${users}`)
      .then(res => res.text())
      .then(r => console.log(r))
      .catch(err => {console.log(err.message)})
    }

    amountup(e){
        this.setState({amount:e.target.value})
    }

    jointup(e){
        this.setState({joins:e.target.value})
    }

    userup(e){
        this.setState({users:e.target.value})
    }

    render(){
        return (
            <form>
            <input placeholder="Amount" onChange={this.amountup}/>
            <input placeholder="Join" onChange={this.joinup}/>
            <input placeholder="Users" onChange={this.userup}/>
            <button class="btn btn-primary" onClick={this.dataup}>Submit</button>
            </form>
        )
    }
}
export default Add;
