import React,{ Component } from 'react';
import Header from './Header';
import '../App.css';
import firebase from './firebaseconfig';

class Register extends Component{
    constructor(props){
        super(props);
        this.state={
            email:null,
            password:null,
            error:null

        }
        this.handleReg=this.handleReg.bind(this);
        this.handleMail=this.handleMail.bind(this);
        this.handlePass=this.handlePass.bind(this);
    }
    handleReg(e){
        var mail=this.state.email;
        var password=this.state.password;
        firebase.auth().createUserWithEmailAndPassword(mail,password).catch(function(err){
            var msg=err.message;
            this.setState({error:msg});
            console.log(err);
        }.bind(this))
    }
    handleMail(e){
        var x=e.target.value;
        this.setState({email:x});
    }
    handlePass(e){
        var x=e.target.value;
        this.setState({password:x});
    }
render(){
    return(
        <div className="container">
        <div className="form" style={{width:"28%",margin:"30vh auto"}}>
            <div className="input-group mb-3 d-flex justify-content-center">
               <img src="logo.png" alt="logo"/>
            </div>
            {(this.state.error) && 
                <div className="input-group mb-3">
                    <textarea type="text" className="text-danger is-invalid bg-none form-control text-danger" value={this.state.error} disabled></textarea>
                </div>
            }
            <div className="input-group mb-3">
                <input type="text" placeholder="Username" className="form-control" />
            </div>
            <div className="input-group mb-3">
                <input type="text" placeholder="Email" className="form-control" onChange={this.handleMail}/>
            </div>
            <div className="input-group mb-3">
                <input type="password" placeholder="Password" className="form-control" onChange={this.handlePass}/>
            </div> 
            <div  className="d-flex justify-content-center">
            <button className="btn btn-outline-success" onClick={this.handleReg}>Sign Up</button>
            </div>
            </div>
            </div>
    );
}
}
class SignUp extends Component {
    render(){
        return(
            <div>
            <Header />
            <Register />
            </div>
        );
    }
}
export default SignUp;
