import React from 'react'
import { FaPlusCircle,FaSmile } from 'react-icons/fa'
function CreateAd(props){
  return(
    <div className="col-sm-7 col-md-9 d-flex justify-content-center align-items-center shadow" style={{width:"50%",height:"100%"}}>
    <FaPlusCircle style={{color:"rgb(0,233,233)",height:"20%",width:"20%"}} />
    <span style={{color:"rgb(0,233,233)"}}>New Ad</span>
    </div> 
  )
}
class Card extends React.Component{
    constructor(props){
        super(props)
        this.state ={
            widthv:0,
            width: "0%",
            users: this.props.users
        }
        this.clicked=this.clicked.bind(this)
    }
    async clicked(){
        var added=(this.state.widthv+(100/this.props.users))
        await this.setState({widthv: added});
        var str=this.state.widthv.toString()+"%"
        this.setState({width: str});
        if(this.state.users>0){
        var newuser=this.state.users-1;
        this.setState({users: newuser })
        }
    }
    render(){
        return (
         <div className="item-card mb-2 shadow">
             <div className="p-2">
             <div className="row text-light">
                <div className="col-1">

                </div>
                <div className="col-7 heading">
                    <div>
                    <span className="d-flex acc">Netflix Account</span>
                    <span className="d-flex vertical-align-center mt-1" style={{fontSize:"12px"}}>â‚¹{this.props.amount}</span>
                    </div>
                </div>
                <div className="col-3 d-flex justify-content-end">

                </div>
                <div className="col-2">

                </div>
             </div>
             <div className="row p-2 no-gutters">
             <div className="col-1">

             </div>
             <div className="col-10">
             <div className="progress" style={{height:"10px"}}>
                <div className="progress-bar bg-warning" role="progressbar" aria-valuenow="75" aria-valuemin="0" aria-valuemax="100" style={{width:this.state.width}}></div>
                </div>
                </div>
             </div>
             <div className="row">
                 <div className="col-1" />
                 <div className="col-5 text-warning">
                   <span style={{fontize:12}}>{this.state.users} users left</span>
                 </div>
                 <div className="col-5 d-flex justify-content-end text-light" >
                    {this.props.users} users
                 </div>
                 <div className="col-1" />
             </div>
             </div>
        </div>
        );
    }
}


function MyAds(props){
  return(
      <div className="col-sm-5 col-md-3" style={{width:"50%",height:"100%"}}>
          <Card />
      </div>
  )
}
class DashBoard extends React.Component{
    constructor(props){
        super(props)
    }
    render(){
        return (
        <div className="container d-flex align-items-center" style={{height:"100vh",width:"100vw"}}>
        <div className="shadow row" style={{backgroundColor:"rgb(71,64,95,0.9)",height:"90vh",width:"90vw"}}>
        <CreateAd />
        <MyAds />
        </div>
        </div>
        )
    }
}

export default DashBoard;
